let pendingList = context.get('pendingList')
if (pendingList.length === 0) { // all tests completed
  context.set('pendingList',null)
  return [msg]
} else { // ongoing tests
  let msg = {} // create new, empty "msg" object

  let chosenIndex  = Math.floor(Math.random()*pendingList.length)
  let chosenOutput = pendingList.splice(chosenIndex,1)[0]

  context.set('pendingList',pendingList)

  let MsgList = context.get('MsgList').slice()
    MsgList[chosenOutput] = msg
  return MsgList
}

if (msg.TestMode === 'show-error') {
  node.error(context.get('failed-msg') || '(no failure)')
//return msg // do NOT pass injected message
} else {
  if (msg.error == null) {
    context.set('failed-msg',null)
    node.status({fill:'green',shape:'dot',text:'ok'})
  } else {
    let failedMsg = Object.assign({},RED.util.cloneMessage(msg))
    context.set('failed-msg',failedMsg)
    node.status({fill:'red',shape:'dot',text:'failed'})

    let Totals = flow.get('Totals')
    flow.set('Totals', typeof Totals === 'number' ? Totals + 1 : 1)
  }

  if (msg.TestMode === 'single-test') {
    return null
  } else {
    return msg
  }
}

